import React, { Component } from "react";
//import SimpleMDE from "react-simplemde-editor";
//import "easymde/dist/easymde.min.css";
//import ReactMarkdown from "react-markdown";
import { Consumer } from "./context";
import { v4 as uuid } from "uuid";

class AddCurrentEvent extends Component {
  state = {
    imageUrl: "",
    cEventName: "",
    about: "",
    time: "",
    date:"",
    submitMessage: "",
    submitMessageTextColor: "",
  };

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };


  onSubmit = (handler, event) => {
    event.preventDefault();

    let isSuccessful = true;

    if (isSuccessful) {
      this.setState({
        submitMessage: `Project published successfully`,
        submitMessageTextColor: "text-info",
      });
    } else {
      this.setState({
        submitMessage: "Publish failed :(",
        submitMessageTextColor: "text-danger",
      });
    }

    const newCurrentEvent = {
      id: uuid(),
      imageUrl: this.state.imageUrl,
      about: this.state.about,
      cEventName: this.state.cEventName,
      date: this.state.date,
      time: this.state.time,
    };

    handler("ADD_CURRENT_EVENT", newCurrentEvent);
  };

  render() {
    return (
      <Consumer>
        {(value) => {
          const {
            imageUrl,
            cEventName,
            //about,
            date,
            time,
            submitMessage,
            submitMessageTextColor,
          } = this.state;
          const { handler } = value;

          return (
            <div className="container-fluid my-5 py-5">
              <h1 className="text-center my-5 font-weight-light">
                Add Current Event
              </h1>
              <div className="row px-3 px-lg-5">
                <div className="col-12 col-lg-6 px-lg-5">
                  <form onSubmit={this.onSubmit.bind(this, handler)}>
                    <div className="form-group">
                      <label htmlFor="imageUrl">Image Url *</label>
                      <input
                        type="text"
                        name="imageUrl"
                        id="imageUrl"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="cEventName">Event Name *</label>
                      <input
                        type="text"
                        name="cEventName"
                        id="cEventName"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="about">About *</label>
                      <input
                        type="text"
                        name="excerpt"
                        id="excerpt"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="date">Date *</label>
                      <input
                        type="text"
                        name="date"
                        id="date"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="time">Time *</label>
                      <input
                        type="text"
                        name="time"
                        id="time"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>

                    <button
                      type="submit"
                      className="btn btn-dark btn-block my-5"
                      style={{ backgroundColor: "black" }}
                    >
                      Publish
                    </button>
                  </form>
                  <div className="text-center">
                    <h5 className={submitMessageTextColor}>{submitMessage}</h5>
                  </div>
                </div>
                <div className="col-12 col-lg-6 markdown">
                  <div className="justify-content-center">
                    <img src={imageUrl} alt={title} />
                  </div>
                  <h1 className="font-weight-light text-center my-5">
                    {title}
                  </h1>
                </div>
              </div>
            </div>
          );
        }}
      </Consumer>
    );
  }
}

export default AddCurrentEvent;
