import React, { Component } from "react";
//import SimpleMDE from "react-simplemde-editor";
//import "easymde/dist/easymde.min.css";
//import ReactMarkdown from "react-markdown";
import { Consumer } from "./context";
import { v4 as uuid } from "uuid";

class AddFutureEvent extends Component {
  state = {
    fEventName: "",
    about: "",
    date: "",
    time: "",
    imageUrl:"",
    regionalOfficeName: "",
    submitMessage: "",
    submitMessageTextColor: "",
  };

  onChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  onSubmit = (handler, event) => {
    event.preventDefault();

    let isSuccessful = true;

    if (isSuccessful) {
      this.setState({
        submitMessage: `Event published successfully`,
        submitMessageTextColor: "text-info",
      });
    } else {
      this.setState({
        submitMessage: "Publish failed :(",
        submitMessageTextColor: "text-danger",
      });
    }

    const newFutureEvent = {
      id: uuid(),
      fEventName: this.state.fEventName,
      about: this.state.about,
      date: this.state.date,
      time: this.state.time,
      imageUrl:this.state.imageUrl,
    };

    handler("ADD_FUTURE_EVENT", newFutureEvent);
  };

  render() {
    return (
      <Consumer>
        {(value) => {
          const {
            fEventName,
            //about,
            date,
            time,
            imageUrl,
            submitMessage,
            submitMessageTextColor,
          } = this.state;
          const { handler } = value;
          return (
            <div className="container-fluid my-5 py-5">
              <h1 className="text-center my-5 font-weight-light">
                Add Future Event
              </h1>
              <div className="row px-3 px-lg-5">
                <div className="col-12 col-lg-6 px-lg-5">
                  <form onSubmit={this.onSubmit.bind(this, handler)}>
                  <div className="form-group">
                      <label htmlFor="imageUrl">Image Url *</label>
                      <input
                        type="text"
                        name="imageUrl"
                        id="imageUrl"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                  <div className="form-group">
                      <label htmlFor="fEventName">Event Name *</label>
                      <input
                        type="text"
                        name="fEventName"
                        id="fEventName"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="regionalOfficeName">Regional Office Name *</label>
                      <input
                        type="text"
                        name="regionalOfficeName"
                        id="regionalOfficeName"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="about">About *</label>
                      <input
                        type="text"
                        name="about"
                        id="about"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="date">Date *</label>
                      <input
                        type="text"
                        name="date"
                        id="date"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="time">Time *</label>
                      <input
                        type="text"
                        name="time"
                        id="time"
                        className="form-control"
                        onChange={this.onChange}
                        required
                      />
                    </div>
                    <div className="d-flex justify-content-around">
                    <button
                      type="submit"
                      className="btn btn-dark "
                      style={{ backgroundColor: "black" }}
                    >
                      Publish
                    </button>
                    <button
                      type="reset"
                      className="btn btn-dark "
                      style={{ backgroundColor: "black" }}
                    >
                      Cancel
                    </button>
                   </div>
                  </form>
                  <div className="text-center">
                    <h5 className={submitMessageTextColor}>{submitMessage}</h5>
                  </div>
                </div>
                <div className="col-12 col-lg-6 markdown">
                  <div className="justify-content-center">
                    <img src={imageUrl} alt={fEventName} />
                  </div>
                  <h1 className="font-weight-light text-center">
                    {fEventName}
                  </h1>
                  <h1 className="font-weight-light text-center ">
                    {date} {time}
                  </h1>
                </div>
              </div>
            </div>
          );
        }}
      </Consumer>
    );
  }
}

export default AddFutureEvent;
