import React from "react";
import PastEventCard from "./PastEventCard";
import FutureEventCard from "./FutureEventCard";
import CurrentEventCard from "./CurrentEventCard";
import { Link } from "react-router-dom";
import { Consumer } from "./context";

function RegionalEvents(props) {
  return (
    <Consumer>
      {(value) => {
        const { rOName} =props;
        const { events } =value;
        const { pastEvents } = value;
        const { futureEvents }=value;
        const { currentEvents }=value;
        const eventsArr = events.filter((roEvent) => roEvent.rname == rOName);
        const sortedActivities = eventsArr.sort((a, b) => new Date(b.edate) - new Date(a.edate));  
        const sdate=new Date();
        const sortedPastEvents=[];
        const sortedFutureEvents=[];
        const sortedCurrentEvents=[];
        for(let i in sortedActivities){
          let dt=new Date(sortedActivities[i].edate);
          if (dt.getTime() < sdate.getTime()){
              sortedPastEvents.push(sortedActivities[i]);
          }
          else if (dt.getTime() > sdate.getTime()){
              sortedFutureEvents.push(sortedActivities[i]);
          }
          else{
              sortedCurrentEvents.push(sortedActivities[i]);
          }             
        }
        //const pastEventsArr = pastEvents.filter((pastEvent) => pastEvent.regionalOfficeName == rOName);
        //const futureEventsArr = futureEvents.filter((futureEvent) => futureEvent.regionalOfficeName == rOName);
        //const currentEventsArr = currentEvents.filter((currentEvent) => currentEvent.regionalOfficeName == rOName);
        return (
          <>
          <div>
             <div className="container text-center my-5">
            <h1 className="font-weight-light py-5">
              Current Events
            </h1>
            <div className="row d-flex justify-content-around my-5 pt-3">
              {currentEventsArr.slice(0,2).map((currentEvent) => (
                <div key={currentEvent.id} className="col-12 col-md-5 my-2">
                  <CurrentEventCard currentEvent={currentEvent} />
                </div>
              ))}
            </div>
          </div>
            <div className="my-5 text-right">
              <Link to={{  pathname: "/allcurrentevents",  state: currentEventsArr }}>
                    See all current events
                    <i className="fas fa-arrow-right align-middle pl-1"></i>
                </Link>
            </div>
          </div>
          
          <div>
            <div className="container text-center my-5">
            <h1 className="font-weight-light">
                Future Events
            </h1>
            <div className="row d-flex justify-content-around my-5 pt-3">
              {futureEventsArr.slice(0,2).map((futureEvent) => (
                <div key={futureEvent.id} className="col-12 col-md-5 my-2">
                  <FutureEventCard futureEvent={futureEvent} />
                </div>
              ))}
            </div>
          </div>
            <div className="my-5 text-right">
              <Link to={{  pathname: "/allfutureevents",  state: futureEventsArr }}>
                    See all future events
                    <i className="fas fa-arrow-right align-middle pl-1"></i>
                </Link>
            </div>
            </div>

          <div className="container text-center my-5">
                <h1 className="font-weight-light">
                    Past Events
                </h1>
                <div className="row d-flex justify-content-around my-5 pt-3">
                    {sortedPastEvents.slice(0,2).map((pastEvent) => (
                        <div key={pastEvent.eid} className="col-12 col-md-5 my-2">
                            <PastEventCard pastEvent={pastEvent} />
                        </div>
                    ))}
                </div>
                <div className="my-5 text-right">
                <Link to={{  pathname: "/allpastevents",  state: sortedPastEvents }}>
                    See all past events
                    <i className="fas fa-arrow-right align-middle pl-1"></i>
                </Link>              
                </div>
          </div>
            
           </>
          
        );
      }}
    </Consumer>
  );
}

export default RegionalEvents;
