import React from "react";
import PastEventCard from "./PastEventCard";
import { Consumer } from "./context";
import { Link } from "react-router-dom";

function ProjectSection() {
  return (
    <Consumer>
      {(value) => {
        const { pastEvents } = value;
        return (
          <div className="container text-center my-5">
            <h1 className="font-weight-light">
              Past Events
            </h1>
            <div className="row my-5 pt-3">
              {pastEvents.slice(0, 3).map((pastEvent) => (
                <div key={pastEvent.id} className="col-12 col-md-4 my-2">
                  <ProjectCard pastEvent={pastEvent} />
                </div>
              ))}
            </div>
            <div className="my-5">
              <Link to="/allprojects" className="text-dark text-right">
                <h5>
                  See all past events
                  <i className="fas fa-arrow-right align-middle pl-1"></i>
                </h5>
              </Link>
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}

export default ProjectSection;
