import React from "react";
//import PastEventSection from "./PastEventSection";
//import FutureEventSection from "./FutureEventSection";
//import CurrentEventSection from "./CurrentEventSection";
import PastEventCard from "./PastEventCard";
import FutureEventCard from "./FutureEventCard";
import CurrentEventCard from "./CurrentEventCard";
import { Link } from "react-router-dom";
import { Consumer } from "./context";
//import RegionalOfficeSection from "./RegionalOfficeSection";

function RegionalEvents(props) {
  return (
    <Consumer>
      {(value) => {
        const { rOName} =props.rOName;
        const { pastEvents } = value;
        const { futureEvents }=value;
        const { currentEvents }=value;
        const pastEventsArr = pastEvents.filter((pastEvent) => pastEvent.regionalOfficeName == rOName);
        return (
          <><div className="container text-center my-5">
                <h1 className="font-weight-light">
                    Past Events
                </h1>
                <div className="row my-5 pt-3">
                    {pastEventsArr.map((pastEvent) => (
                        <div key={pastEvent.id} className="col-12 col-md-4 my-2">
                            <PastEventCard pastEvent={pastEvent} />
                        </div>
                    ))}
                </div>
                <div className="my-5">
                    <Link to="/allpastevents" className="text-dark text-right">
                        <h5>
                            See all past events
                            <i className="fas fa-arrow-right align-middle pl-1"></i>
                        </h5>
                    </Link>
                </div>
            </div>
            
           </>
          
        );
      }}
    </Consumer>
  );
}

export default RegionalEvents;
