import React from "react";
import { Consumer } from "./context";
import FutureEventCard from "./FutureEventCard";

function AllFutureEvents(props) {
  return (
    <Consumer>
      {(value) => {
        const futureEventsArr  = props.location.state;
        const sortedActivities = futureEventsArr.sort((a, b) => new Date(b.date) - new Date(a.date));  
        const sdate=new Date();
        const sortedFutureEvents=[];
        for(let i in sortedActivities){
          let dt=new Date(sortedActivities[i].date);
          if (dt.getTime() > sdate.getTime()){
              sortedFutureEvents.push(sortedActivities[i]);
          }             
        }
        return (
          <div className="container text-center my-5 py-5">
            <h1 className="font-weight-light py-5">
              All Future Events
            </h1>
            <div className="row my-4 pt-4">
              {sortedFutureEvents.map((futureEvent) => (
                <div key={futureEvent.id} className="col-12 col-md-6 py-3">
                  <FutureEventCard futureEvent={futureEvent} />
                </div>
              ))}
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}

export default AllFutureEvents;
