import React from "react";
import { Consumer } from "./context";
import CurrentEventCard from "./CurrentEventCard";

function AllCurrentEvents(props) {
  return (
    <Consumer>
      {(value) => {
        const currentEventsArr  = props.location.state;
        const sortedActivities = currentEventsArr.sort((a, b) => new Date(b.date) - new Date(a.date));  
        const sdate=new Date();
        const sortedCurrentEvents=[];
        for(let i in sortedActivities){
          let dt=new Date(sortedActivities[i].date);
          if (dt.getTime() < sdate.getTime()){
              sortedCurrentEvents.push(sortedActivities[i]);
          }             
        }
        return (
          <div className="container text-center my-5 py-5">
            <h1 className="font-weight-light py-5">
              All Current Events
            </h1>
            <div className="row my-4 pt-4">
              {currentEventsArr.map((currentEvent) => (
                <div key={currentEvent.id} className="col-12 col-md-6 py-3">
                  <CurrentEventCard currentEvent={currentEvent} />
                </div>
              ))}
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}

export default AllCurrentEvents;
