import React from "react";
import { Consumer } from "./context";
//import ReactMarkdown from "react-markdown";

function PastEventPage(props) {
  return (
    <Consumer>
      {(value) => {
        const { pastEvents } = value;
        const id = props.match.params.id;
        const pastEvent = pastEvents.filter((pastEvent) => pastEvent.id == id)[0];
        const {eimg, ename, rname, edesc, edate, etime,noOfStudentsParticipated,ewin} = pastEvent;
        return (
          <div className="container py-5 my-5 markdown">
            <div className="row justify-content-center">
              <div className="col-12 col-md-6 ">
                <img src={imageUrl} alt={pEventName} className="w-100" />
              </div>
            </div>
            <h1 className="font-weight-light text-center my-5">Event Name: {pEventName}</h1>
            <h1 className="font-weight-light text-center my-5">Organized by: {regionalOfficeName}</h1>
            <h1 className="font-weight-light text-center my-5">About: {about}</h1>
            <h1 className="font-weight-light text-center my-5">No.of students Participated: {noOfStudentsParticipated}</h1>
            <h1 className="font-weight-light text-center my-5">Winner: {winner}</h1>
            <h1 className="font-weight-light text-center my-5">Date: {date}</h1>
            <h1 className="font-weight-light text-center my-5">Time: {time}</h1>
          </div>
        );
      }}
    </Consumer>
  );
}

export default PastEventPage;
