import React, {useState} from "react";
import PastEventCard from "./PastEventCard";
import FutureEventCard from "./FutureEventCard";
import CurrentEventCard from "./CurrentEventCard";
import { Link } from "react-router-dom";
import { Consumer } from "./context";
import AllPastEvents from "./AllPastEvents";

function RegionalEvents(props) {
  //const [state, setstate] = useState({data:props})
   //
  return (
    <Consumer>
      {(value) => {
        const { rOName} =props;
        const { pastEvents } = value;
        const { futureEvents }=value;
        const { currentEvents }=value;
        const pastEventsArr = pastEvents.filter((pastEvent) => pastEvent.regionalOfficeName == rOName);
        const futureEventsArr = futureEvents.filter((futureEvent) => futureEvent.regionalOfficeName == rOName);
        const currentEventsArr = currentEvents.filter((currentEvent) => currentEvent.regionalOfficeName == rOName);
        return (
          <>
          <div>
             <div className="container text-center my-5 py-5">
            <h1 className="font-weight-light py-5">
              Current Events
            </h1>
            <div className="row d-flex justify-content-around my-4 pt-4">
              {currentEventsArr.slice(0,3).map((currentEvent) => (
                <div key={currentEvent.id} className="col-12 col-md-6 py-3">
                  <CurrentEventCard currentEvent={currentEvent} />
                </div>
              ))}
            </div>
          </div>
            <div className="my-5">
              <Link to="/allcurrentevents" className="text-dark text-right">
                <h5>
                  See all current events
                  <i className="fas fa-arrow-right align-middle pl-1"></i>
                </h5>
              </Link>
            </div>
          </div>
          
          <div className="container text-center my-5">
                <h1 className="font-weight-light">
                    Past Events
                </h1>
                <div className="row d-flex justify-content-around my-5 pt-3">
                    {pastEventsArr.slice(0,3).map((pastEvent) => (
                        <div key={pastEvent.id} className="col-12 col-md-4 my-2">
                            <PastEventCard pastEvent={pastEvent} />
                        </div>
                    ))}
                </div>
                <div className="my-5">
                    
                        <Link
  to={{
    pathname: "/allpastevents",
    data: pastEventsArr
  }}
>All past events
                    </Link>
                </div>
            </div>
            <div>
            <div className="container text-center my-5 py-5">
            <h1 className="font-weight-light py-5">
                Future Events
            </h1>
            <div className="row d-flex justify-content-around my-4 pt-4">
              {futureEventsArr.slice(0,3).map((futureEvent) => (
                <div key={futureEvent.id} className="col-12 col-md-6 py-3">
                  <FutureEventCard futureEvent={futureEvent} />
                </div>
              ))}
            </div>
          </div>
            <div className="my-5">
              <Link to="/allcurrentevents" className="text-dark text-right">
                <h5>
                  See all current events
                  <i className="fas fa-arrow-right align-middle pl-1"></i>
                </h5>
              </Link>
            </div>
            </div>
           </>
          
        );
      }}
    </Consumer>
  );
}

export default RegionalEvents;
