import React from "react";
import { Consumer } from "./context";
import RegionalEvents from "./RegionalEvents";
import PastEventCard from "./PastEventCard";
import FutureEventCard from "./FutureEventCard";
import CurrentEventCard from "./CurrentEventCard";
//import ReactMarkdown from "react-markdown";

function RegionalOfficePage(props) {
  return (
    <Consumer>
      {(value) => {
        const { regionalOffices } = value;
        const { pastEvents } = value;
        const { currentEvents } = value;
        const { futureEvents } = value;
        const id = props.match.params.id;

        const regionalOffice = regionalOffices.filter((regionalOffice) => regionalOffice.id == id)[0];
        const { imageUrl, regionalOfficeName,location } = regionalOffice;
        const rOName = regionalOfficeName;
        return (
          <div className="container py-5 my-5 markdown">
            <div className="justify-content-center">
              <img src={imageUrl} alt={regionalOfficeName} className="w-100" />
            </div>
            <h1 className="font-weight-light text-center my-5">{regionalOfficeName}</h1>
            <h1 className="font-weight-light text-center my-5">{location}</h1>
            {console.log(rOName)}
            <div className="p-5">
                <RegionalEvents rOName={rOName}/>
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}

export default RegionalOfficePage;
