import React from "react";
import CurrentEventCard from "./CurrentEventCard";
import { Consumer } from "./context";
import { Link } from "react-router-dom";

function CurrentEventSection() {
  return (
    <Consumer>
      {(value) => {
        const { currentEvents } = value;
        return (
          <div className="container text-center my-5">
            <h1 className="font-weight-light">
              Current Events
            </h1>
            <div className="row my-5 pt-3">
              {currentEvents.slice(0, 3).map((currentEvent) => (
                <div key={currentEvent.id} className="col-12 col-md-4 my-2">
                  <CurrentEventCard currentEvent={currentEvent} />
                </div>
              ))}
            </div>
            <div className="my-5">
              <Link to="/allprojects" className="text-dark text-right">
                <h5>
                  See all current events
                  <i className="fas fa-arrow-right align-middle pl-1"></i>
                </h5>
              </Link>
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}

export default CurrentEventSection;
