import React from "react";
import { Consumer } from "./context";
//import ReactMarkdown from "react-markdown";

function CurrentEventPage(props) {
  return (
    <Consumer>
      {(value) => {
        const { currentEvents } = value;
        const id = props.match.params.id;
        const currentEvent = currentEvents.filter((currentEvent) => currentEvent.id == id)[0];
        const { imageUrl, cEventName, about, date, time } = currentEvent;
        return (
          <div className="container py-5 my-5 markdown">
            <div className="justify-content-center">
              <img src={imageUrl} alt={cEventName} className="w-100" />
            </div>
            <h1 className="font-weight-light text-center my-5">{cEventName}</h1>
            <h1 className="font-weight-light text-center my-5">{about}</h1>
            <h1 className="font-weight-light text-center my-5">{date}</h1>
            <h1 className="font-weight-light text-center my-5">{time}</h1>
          </div>
        );
      }}
    </Consumer>
  );
}

export default CurrentEventPage;
